import {useState} from 'react';
import './Counter.css';

function Counter(){
    const [count,setCount]=useState(0);

    const incrementCount=()=>{
        setCount(count+1);
    }

    const decrementCount=()=>{
        if(count===0){
            setCount(0);
        }
        setCount(count-1);
    }

    return(
        <div className="counter-contai">
            <h1>Counter App</h1>
            <div>{count}</div>
            <div>
                <button>+</button>
                <button>-</button>
            </div>
        </div>
    );
}

export default Counter;
